import React from 'react';
import ProductsHeader from './ProductsHeader';
import ProductsList from './ProductsList';

const productsEndpoint = '/products';

export class Products extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            products: [],
            error: false
        };
    }


    async componentDidMount(){
            const data = await fetch("http://localhost:4001/api/products");
            const jsonData = await data.json();
            this.setState({ products: jsonData});
        // await fetch("http://localhost:4001/api/products").then( response => this.setState({products: response.data })).catch( error =>  console.log)

    }
    render(){
            /*
              decide if filter is on show
              filtler array if not on all products
              have to go from filter and no filter
            */
             const mappedProducts = this.state.products.map((product, index) => {
                  return(  <div className="col-md-4">
                      <div className="card mb-4 box-shadow">
                        <img id={index} src={product.product_image} alt={product.product_description}/>
                           <div className="card-body">
                        <p className="card-text font-weight-bold lead">{product.product_name}</p>
                        <p className="card-text">{product.description}</p>
                          <div className="d-flex justify-content-between align-items-center">
                            <div className="btn-group">
                              <button type="button" className="btn btn-sm btn-outline-secondary">View</button>
                              <button type="button" className="btn btn-sm btn-outline-secondary">Buy</button>
                            </div>
                            <large className="text-muted font-weight-bold lead">${product.price}</large>
                          </div>
                        </div>
                      </div>
                    </div>
                  )
             })
            return(
              // Products
              <div>
              <select onChange={this.handleFilter}>
                <option value="Necklaces">Necklaces</option>
                <option value="Rings">Rings</option>
                <option value="Watches">Watches</option>
              </select>

                <div className="album py-5 bg-light">
                  <div className="container">
                    <div className="row">
                      {mappedProducts}
                    </div>
                  </div>
                </div>
                </div>
          )
          }

        }
// const Products = () => {
//     return (
//         <React.Fragment >
//             <ProductsHeader />
//             <ProductsList />
//         </React.Fragment>
//     );
// }

export default Products;
