import React from 'react';
import ProductsHeader from './ProductsHeader';
import ProductsList from './ProductsList';

const productsEndpoint = '/products';
const initialState = {
    products: [],
    error: false
};

export class Products extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            products: [],
            error: false
        };
    }
    async componentDidMount(){
            // const data = await fetch("http://localhost:4001/api/products", {mode: "no-cors"});
            // const jsonData = await data.json();
            // this.setState =({ products: jsonData});
        await fetch("http://localhost:4001/api/products", {mode: "no-cors"}).then( response => response.json()).then( data => this.setState({products: data})).catch( error =>  this.setState({error: false}))

    }
    render(){
        console.log("state: ", this);
        return(

            <div>


                {this.state.products.map(e =>{
                    return(
                        <div>
                            <p>{e.name}</p>
                            <img scr={e['product_image']}></img>
                            <button></button>
                        </div>
                    )
                })}
            </div>
        )
    }
}

