import React from 'react';
import ProductsHeader from './ProductsHeader';
import ProductsList from './ProductsList';

const productsEndpoint = '/products';

export class Products extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            products: [],
            error: false
        };
    }


    async componentDidMount(){
            const data = await fetch("http://localhost:4001/api/products");
            const jsonData = await data.json();
            this.setState({ products: jsonData});
        // await fetch("http://localhost:4001/api/products").then( response => this.setState({products: response.data })).catch( error =>  console.log)

    }
    render(){
        console.log(this);
        return(

            <div>


                {this.state.products.map(e =>{
                    return(
                        <div>
                            <p>{e.name}</p>
                            <img scr={e['product_image']}></img>
                            <button></button>
                        </div>
                    )
                })}
            </div>
        )
    }
}

